import styled from 'styled-components';
import { themeGet } from 'styled-system';

const SignupWrapper = styled.div`
  position: relative;
  background-color: ${themeGet('colors.primary', '#F2B306')};
  height: 100vh;
  @media (max-width: 1099px) {
    overflow: hidden;
  }
  .button__wrapper {
    @media only screen and (max-width: 480px) {
      text-align: center;
    }
  }
  .reusecore__button {
    .btn-icon {
      color: ${themeGet('colors.white', '#ffffff')};
      font-size: 18px;
      @media only screen and (max-width: 1100px) {
        color: ${themeGet('colors.primary', '#F2B306')};
      }
      @media only screen and (max-width: 420px) {
        font-size: 14px;
      }
    }
    &:hover {
      background: transparent;
      box-shadow: none;
    }
  }
  .col {
    position: relative;
    .patternImage {
      // position: absolute;
      // width: 100%;
      // height: 100%;
      // object-fit: cover;
    }
    @media only screen and (max-width: 991px) {
      width: 100%;
      &.imageCol {
        background-color: ${themeGet('colors.primary', '#F2B306')};
        display: flex;
        flex: 1;
      }
    }
  }
  .reusecore__button {
    background-color: transparent;
    &.default {
      background-color: rgb(26, 115, 232);
      transition: all 0.3s ease;
      &:hover {
        box-shadow: 0px 9px 20px -5px rgba(26, 115, 232, 0.57);
      }
    }
  }
  .reusecore__button {
    transition: all 0.3s ease;
    cursor: pointer;
    .btn-icon {
      display: -webkit-box;
      display: -webkit-flex;
      display: -ms-flexbox;
      display: flex;
      color: rgb(26, 115, 232);
      width: 35px;
    }
    &:hover {
      box-shadow: 0px 9px 20px -5px rgba(43, 43, 43, 0.57);
      background-color: #2B2B2B;
      cursor: pointer;
    }
    &.btn-secondary {
      @media (max-width: 460px) {
        margin-top: 10px;
        margin-left: 0;
        border: 1px solid #ffffff;
        min-width: auto;
      }
      &:hover {
        opacity: 0.85;
        background-color: rgb(255, 255, 255) !important;
        cursor: pointer;
        box-shadow: 0px 9px 20px -5px rgba(255, 255, 255, 0.57);
      }
    }
  }
`;

export default SignupWrapper;
